document.addEventListener(
  "DOMContentLoaded",
  () => {
    let d = $("body")
        .find("#downloadBFPSuccess")
        .children()
        .children()
        .children("p"),
      e = $("body")
        .find("#downloadBFPSuccess")
        .children()
        .children()
        .children("a"),
      o = setTimeout(() => {
        $("body")
          .addClass("modal-open")
          .find(".modal.fade")
          .addClass("show")
          .removeAttr("aria-hidden")
          .css("display", "block")
          .find(".modal-header")
          .find("h5")
          .text("ENQUIRE NOW");
        let d = $("body").find("#downloadBFPSuccess").children().children();
        d.children("p").remove(), d.children("a").remove();
      }, 1e4);
    $('[data-dismiss="modal"]').on("click", () => {
      $("body")
        .removeClass("modal-open")
        .find(".modal.fade")
        .removeClass("show")
        .attr("aria-hidden", !0)
        .css("display", "none")
        .find(".modal-header")
        .find("h5")
        .text("Submit Your Details to Download Brochure & Floor Plan");
      let n = $("body")
        .find("#downloadBFPSuccess")
        .children()
        .children()
        .children(".text-success");
      $("body").find("#downloadBFPSuccess").children().children().append(n, d),
        $("body")
          .find("#downloadBFPSuccess")
          .children()
          .children()
          .append(n, e),
        $('[data-dismiss="modal"]').off("click"),
        $(".downloadBFPForm").trigger("reset"),
        clearTimeout(o);
    });
  },
  !1,
);
